+- (void)testGBDocListData_laterChangeFeedFetch {
+    NSData *pathData = [NSKeyedArchiver archivedDataWithRootObject:[@"/tmp" stringByAppendingString:@"/GBox"]];
+
+    [[NSUserDefaults standardUserDefaults] setObject:pathData forKey:@"GBoxFolderPath"];
+    [[NSUserDefaults standardUserDefaults] synchronize];
+
+    [[OAuthController authController] tryKeychainAuthenticaiton];
+    
+    GBDocListData *dld = [GBDocListData sharedDocListData];
+    [dld setAuth:[[OAuthController authController] auth]];
+    [dld laterChangeFeedFetch:1];
+    
+    do {
+        [[NSRunLoop currentRunLoop] runMode:NSDefaultRunLoopMode beforeDate:[NSDate distantFuture]];
+    } while (true);
+}
